import React, { useState } from 'react'
import { Link } from 'react-router-dom'

import './styles/UserCreate.css'

export default function UserCreate(props) {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
      })
      const { username, password } = formData;
      const {handleLogin} = props;

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
          ...prevState,
          [name]: value
        }))
      }

      const handleSubmit = (e) => {
          e.preventDefault();

      }
    return (
        <div className="user-create-wrapper">
            <form className="user-create-form" onSubmit={(e) => {
      handleLogin(formData)
    }}>
                <div className="user-create-form-name">Create an account</div> 
                <input className='user-create-input' type="text" placeholder='username' onChange={handleChange} />
                <input className='user-create-input' type="text" placeholder='email' onChange={handleChange} />
                <input className='user-create-input' type="text" placeholder='password' onChange={handleChange} />
                <input className='user-create-input' type="text" placeholder='confirm password' />
                <input className='user-create-submit' type="submit" value="submit"/>
                <div>Have an account? <Link to='/login'>Login</Link></div>
            </form>
        </div>
    )
}
